/*
 src/actions/appAction.js
*/

import fetch from 'cross-fetch'

export const actions = {

  changePageView: target => ({
    type: 'TOGGLE_PAGE_VIEW',
    target
  }),

  // ADMIN LOGIN

  adminUsername: value => ({
    type: 'CHANGE_ADMIN_USERNAME',
    value,
  }),

  adminPassword: value => ({
    type: 'CHANGE_ADMIN_PASSWORD',
    value,
  }),

  bandmemberVerification: credentials => ({
    type: 'BANDMEMBER_VERIFICATION',
    credentials
  }),

  bandmemberLogin: () => dispatch => {
    fetch('/loginCreds')
    .then(response => response.json(), 
         error => console.log(error))
    .then(json => dispatch(actions.bandmemberVerification(json)))
  },

  // EVENTS

  stagedEventName: value => ({
    type: 'ADD_STAGED_EVENT_NAME',
    value
  }),

  stagedEventStart: value => ({
    type: 'ADD_STAGED_EVENT_START',
    value
  }),

  stagedEventEnd: value => ({
    type: 'ADD_STAGED_EVENT_END',
    value
  }),

  addEvent: () => ({
    type: 'ADD_CALENDAR_EVENT',
  }),

  // NEWS

  addNews: () => ({
    type: 'ADD_NEWS_ENTRY',
  }),

  addNewsTitle: value => ({
    type: 'ADD_NEWS_TITLE',
    value
  }),

  addNewsBody: value => ({
    type: 'ADD_NEWS_BODY',
    value
  }),


}
